import java.util.Scanner;
public class for04 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn=new Scanner(System.in);
		System.out.println("請輸入n");
		int n=scn.nextInt();
		for(int j=0;j<n;j++){
			for(int k=0;k<=j;k++){
				System.out.print("*");
			}
			System.out.println();			
		}
	}
}
